import React from 'react'
import Nav from 'react-bootstrap/Nav'
import { Link } from 'react-router-dom';
import UrlCall from './../ContextApi/UrlCall'
import Dropdown from 'react-bootstrap/Dropdown'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

function Header() {
    let apiCall = UrlCall();
    //rated; popularity; favorites; recommendations; watchlist
    let makeApiCall = (e) => {
        // apiCall.getCustomCall(menu);
        switch (e.target.text) {
            case "Popular": {
                apiCall.getCustomCall('popularity');
                break;
            }
            case "Trending": {
                apiCall.getCustomCall('recommendations');
                break;
            }
            case "Newest": {
                apiCall.getCustomCall('watchlist');
                break;
            }
            case "Highest Rated": {
                apiCall.getCustomCall('rated');
                break;
            }
            default: {
                apiCall.getCustomCall('popular');
            }
        }
    }
    return (
        <Nav>
            <Nav.Item>
                <Nav.Link disabled href="/home"><b>SERIEX</b></Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link onClick={makeApiCall} eventKey="link-1">
                    <Link to="/">Popular</Link>
                </Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link onClick={makeApiCall} eventKey="link-1">
                    <Link to="/">Trending</Link>
                </Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link onClick={makeApiCall} eventKey="link-1">
                    <Link to="/">Newest</Link>
                </Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link onClick={makeApiCall} eventKey="link-1">
                    <Link to="/">Highest Rated</Link>
                </Nav.Link>
            </Nav.Item>

            <Dropdown>
                <Dropdown.Toggle variant="default" id="dropdown-basic">
                    Filter
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Type of media</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Genre</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Rating</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>

            <Form inline>
                <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                <Button variant="outline-success">Search</Button>
            </Form>
        </Nav >
    );
}

export default Header;
