import { useContext, useEffect } from 'react';
import { BaseContext } from './BaseContext'

const UseBaseContext = () => {
    const [state, setState] = useContext(BaseContext);

    function addToEmissionFood(id, value) {
        setState(state => ({
            ...state, emissionTotal: {
                ...state.emissionTotal, id: value
            }
        }));
        console.log(state.emissionTotal);
    }

    function resetEmissionTotal() {
        setState(state => ({
            ...state, emissionTotal: {
                energy: 0,
                food: 0,
                travel: 0,
                house: 0
            }
        }));
    }

    function totalEmission() {
        return state.emissionTotal.house + state.emissionTotal.travel + state.emissionTotal.food + state.emissionTotal.energy;
    }

    return {
        addToEmission,
        totalEmission: totalEmission(),
        foodEmission: state.emissionTotal.food,
        energyEmission: state.emissionTotal.energy,
        houseEmission: state.emissionTotal.house,
        travelEmission: state.emissionTotal.travel,
        resetEmissionTotal: resetEmissionTotal()
    }
}


export default UseBaseContext;
